INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_01964ac6.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_d176e087.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_69e3a6a9.jpg','Затем рубленный чеснок.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_46ec39d8.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_5a2c6ce7.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_038fdef4.jpg','Добавьте томатную пасту.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_b8dad03e.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_64fda030.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_abd949bb.jpg','И все хорошенько перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_35e2a5c0.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_f2b15587.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_87492250.jpg','Полейте маринадом куски курицы.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_7085d754.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_65271be1.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_8717dae9.jpg','И оставьте мясо мариноваться минимум на 2-3 часа.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_437e0675.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_6a305c94.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_3eba38ec.jpg','Обжарьте на гриле с двух сторон.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1683,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_2f0db7b5.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_85582989.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_ab70223f.jpg','\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_5e46296c.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_f2980f86.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_421e2e91.jpg','В миску налейте оливковое масло и сухое белое вино. Добавьте красный перец и чеснок. Хорошенько все перемешайте','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_6850a3ac.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_80d356a5.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_801b0e6d.jpg','На пергамент выложите ломтики лимона, сверху выложите стейк тунца и третьем слоем ломтики лимона. Полить масляной смесью, заверните пергамент конвертиком и запекайте в духовке до готовности.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_2fbc16fb.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_9f9894da.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_33763848.jpg','Сварите пасту.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_8cd59f5d.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_41c82901.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_f637e848.jpg','Нарежьте петрушку.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_fc54012d.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_ec8d60c4.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_6331d872.jpg','После духовки аккуратно достаньте из пергамента тунец.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_505e7c97.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_3d5e6ac5.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_c146a07d.jpg','Тунец разломайте на кусочки.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_06086495.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_95304234.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_a25e4cde.jpg','На готовую пасту выложите петрушку.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_76df70a8.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_56a73875.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_39e8865a.jpg','Затем добавьте тунец.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_22bb546c.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_abf63369.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_9ad60c41.jpg','Полить масляной смесью, сверху положите стружку сыра, посолите и поперчите по вкусу. Затем все перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1685,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_16651f28.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_6ed8df63.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_daff38c6.jpg','Паста готова.<br><br>Приятного аппетита!','');
