INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1711,'http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/m_202936db.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/x_88b90ed5.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/s_5a89da38.jpg','Залейте пиво, хорошенько перемешайте и оставьте на несколько часов при комнатной температуре, пока сыр не станет мягким.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1711,'http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/m_7874b295.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/x_304d3857.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/s_fb12f0be.jpg','В блендер выложите сырную смесь и добавьте размягченное сливочное масло. Смешайте до однородного состояния.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1711,'http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/m_3e2ca28c.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/x_6f25d73e.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/s_a13354f4.jpg','Добавьте вустерширский соус, копченую паприку, перец и корицу. Хорошенько все перемешайте','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1711,'http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/m_ed78f9d7.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/x_a8e55a8f.jpg','http:\/\/cs11191.vkontakte.ru\/u6208554\/-14\/s_5221b202.jpg','Поместите полученную смесь в баночку, тщательно прижмите ложкой, чтобы убрать воздух.','');
