INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1717,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_e357444e.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_14167f03.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_5c025f90.jpg','Выложите рыбу на доску и снимите с нее кожуру','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1717,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_f1cabe9c.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_92542be2.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_596d3620.jpg','Мелко нарежьте лук, залейте его красным вином и кипятите на медленном огне','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1717,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_c326edb0.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_0e07661c.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_18016997.jpg','Затем добавьте оливковое масло, лимонный сок и немного соли','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1717,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_f27dee8c.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_290aebb4.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_c5d1de61.jpg','В конце полейте форель красным вином с луком. Блюдо готово!<br \/>\n<br \/>\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1718,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_b5a91495.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_03fcbc06.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_409756d1.jpg','Нарубите куриную грудку, выложите миску и засыпьте 1 столовой ложкой специй (гарам масала). Добавьте 1\/2-1\/4 чайной ложки молотого черного перца, посолите. Перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1718,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_1f7fa3cc.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_7959137a.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_339ea20a.jpg','Всыпьте муку и обваляйте в этой смеси курицу.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1718,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_0346f70b.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_0bc5472e.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_46d1910d.jpg','Нагрейте сковороду (1 столовая ложка масла) и обжарьте курицу до полной готовности. Курицу переложите на тарелку.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1718,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_ccafc35f.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_3bbff249.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_e9bfdbc7.jpg','Огонь уменьшите и в ту же сковороду забросьте мелко нарезанный лук, обжарьте (3 минуты), чеснок и имбирь.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1718,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_767c4e38.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_9b2b4365.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_4db0ac37.jpg','Выложите томаты (без сока).Влейте молоко и добавьте 2 остальные ложки специй, соль и перец (по вкусу). Доведите томатную массу до кипения и, уменьшив огонь, тушите минут 15. Соус должен загустеть. Верните курицу в сковороду и готовьте 2-3 минуты.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1718,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_d15b0958.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_f094d813.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_bfe93b6c.jpg','Подавайте с рисом и свежей кинзой.<br><br>Приятного аппетита!','');
