INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1725,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_6a885c92.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_b97c85a1.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_0844f509.jpg','Перелейте в стеклянную емкость с плотной крышкой. Поставьте в прохладное темное место на 10 дней. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1725,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_672304a0.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_83ce1f2d.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_1de3fb16.jpg','Каждый день взбалтывайте бутылку. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1725,'http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/m_cd2847fe.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/x_d15243b4.jpg','http:\/\/cs4444.vkontakte.ru\/u6624865\/-14\/s_9a39d082.jpg','Через 10 дней ликер готов, пропустите через марлю несколько раз (минимум 3 раза). Чем больше, тем прозрачнее и чище ликер.','');
