INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1737,'http:\/\/cs4479.vkontakte.ru\/u6624865\/-14\/m_ea44d7b8.jpg','http:\/\/cs4479.vkontakte.ru\/u6624865\/-14\/x_d2728af9.jpg','http:\/\/cs4479.vkontakte.ru\/u6624865\/-14\/s_a2324c49.jpg','Яйца взбейте с сахаром. Добавьте муку со сметаной, ванилин, соль. Погасите соду и перемешайте все до однородной массы.','');
