INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1737,'http:\/\/cs4479.vkontakte.ru\/u6624865\/-14\/m_e1d711ab.jpg','http:\/\/cs4479.vkontakte.ru\/u6624865\/-14\/x_c21c21df.jpg','http:\/\/cs4479.vkontakte.ru\/u6624865\/-14\/s_f75ca61d.jpg','Подавайте с кленовым сиропом, медом, вареньем, джемом, или сметаной.','');
