INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1741,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_46f3ddfb.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_e11c4247.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_17b7a3b9.jpg','Добавьте соль и перец.','');
