INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1741,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_03113cec.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_17757e15.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_d6b136d3.jpg','Тщательно перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1741,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_2b8fb4cf.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_12054629.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_5cae5314.jpg','Батон разрежьте вдоль на 2 части.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1741,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_22b78a5d.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_da39432f.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_9118f0af.jpg','Намажьте приготовленной смесью и отправьте запекать в духовку на 5-7 минут при температуре 180°C.<br\/>\n<br\/>\nПриятного аппетита!','');
