INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1745,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_d463abc8.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_a3caf58c.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_e0c7690f.jpg','Выложите слой шпината.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1745,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_af949559.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_2e9fe2b6.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_9ca7fec3.jpg','Скрутите рулет. Смажьте сливками рулет по всей поверхности и выпекайте около 25 минут. Подавайте с томат пастой.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1745,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_3a2b3993.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_63b81e0b.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_bc3dfe0d.jpg','Подавайте с томат пастой.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1745,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_b76e7445.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_65fe9281.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_2e2e372b.jpg','Приятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1746,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_b7402710.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_a8b76864.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_5804107a.jpg','Для крема сливки нагреть, растопить в них шоколад, добавить клубнику. Охладить, накрыть плёнкой и поставить на ночь в холодильник. Соединить пудру с миндальной мукой и ставим в разогретую духовку (140°С) для просушки. Затем просеиваем через мелкое сито.\n\n<br\/>\n<br\/>Взбить белки до образования устойчивой пены, затем тонкой струйкой всыпаем сахарную пудру и опять взбиваем. Лопаточкой аккуратно вмешиваем белки в сахарно-миндальную массу и перемешиваем.Добавляем сахар, ванилин, краситель. Смешиваем.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1746,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_ee9bc058.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_73250a7b.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_af1026d3.jpg','На противень, покрытый пергаментной бумагой, выдавливаем через кондитерский мешок с плоской круглой насадкой диаметром 8 мм кружочки не более 3 см в диаметре.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1746,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_0b4c7cdb.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_408aea93.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_43404462.jpg','Ставим противень в духовку, остывшую до 50°С, и держим там макаруны примерно 30 минут - на них должна образоваться плотная плёнка. Затем нагреваем духовку до 140°С\n и ставим выпекать на 12 минут, при этом через 6 минут переворачиваем противень на 180°С.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1746,'http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/m_cf4b3e21.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/x_9590814f.jpg','http:\/\/cs4577.vkontakte.ru\/u6624865\/-14\/s_1c5ddb41.jpg','Достаём, охлаждаем и прослаиваем кремом.<br\/>\n<br\/>\n\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_645aaf34.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_7e23e9a0.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_cb7910dc.jpg','','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_90bcbf1b.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_88e3b8c9.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_96d41c05.jpg','Сыр натрите.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_022012a6.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_68dc130d.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_d72c30ac.jpg','Вымойте брокколи, высушите и разделите на небольшие соцветия.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_e1998df5.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_9b1dd47d.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_f2421eb2.jpg','Забросьте брокколи в кастрюлю с подсоленной кипящей водой и оставьте повариться на 3 минуты.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_58e05c9f.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_8c9bc88d.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_dec8bc73.jpg','Немедля переложите соцветия из кипящей воды в миску со льдом или холодной водой.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_1eba1307.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_f0907caa.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_92fa44b5.jpg','Вымойте, очистите и нарежьте морковь.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_b8bf5da9.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_76b0959c.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_9bcb6392.jpg','Подтушите лук и морковь на сливочном масле (1-2 столовых ложки). Посолите и поперчите. Лук должен позолотиться. Не передержите, овощи должны сохранить форму.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_6129590f.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_e265365c.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_e229d85c.jpg','В кастрюле растопите 60 г масла. Добавьте 1\/4 стакана муки. Помешивая держите около 3-4 минут. Влейте смесь молока и сливок, затем-бульон. Добавьте мускатный орех и под крышкой готовьте около 20 минут,после вбросьте лук, морковь и брокколи и варите 30 мин.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_5bd9eba4.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_c08d47ea.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_8751682d.jpg','Через 25-30 минут, снимите крышку и толкушкой для пюре разомните овощи.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1747,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_d6c71ec8.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_7688e1db.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_147009e8.jpg','Добавьте половину натертого сыра. Перемешайте, чтобы сыр успел расплавиться. При подаче сверху порционно можно также присыпать сыром.<br \/>\n<br \/>\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1749,'http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/m_134502f7.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/x_9fa2af5d.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/s_cc4863a2.jpg','Яйца отварите и остывшими очистите от скорлупы.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1749,'http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/m_97b4c5b5.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/x_d76134ee.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/s_a03b394b.jpg','Разрежьте напополам каждое яйцо и аккуратно достаньте желток.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1749,'http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/m_119d6a18.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/x_3fcfa73d.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/s_522c9b41.jpg','Желтки вилкой разомните и смешайте с майонезом, дижонской горчицей, соусом,лимонным соком и петрушкой до однородной массы.Посолите и поперчите.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1749,'http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/m_08d7b585.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/x_68cb14a1.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/s_8e3e2b17.jpg','Яйца заполните начинкой (можно с помощью кондитерского шприца).','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1749,'http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/m_a919b4c7.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/x_95a2ab04.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/s_6eb4df96.jpg','Нагрейте на сковороде оливковое масло.Обжарьте мелко нарезанный анчоус, нарубленный чеснок.Добавьте цедру и сухари. Помешивая готовьте 2-3 минуты до золотистой корочки. Смешайте с тертым пармезаном и посыпьте этой смесью начиненные яйца.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1749,'http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/m_9dffacc1.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/x_950c1797.jpg','http:\/\/cs5158.vkontakte.ru\/u6624865\/-14\/s_89a1fd5a.jpg','Выложите на блюдо, застеленной листьями салата.<br \/>\n<br \/>\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1750,'http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/m_204b8f93.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/x_f019b84c.jpg','http:\/\/cs302209.vkontakte.ru\/u6208554\/-14\/s_6f6564e5.jpg','','');
