INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1791,'http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/m_fe5363a8.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/x_85b50a18.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/s_6db07c12.jpg','','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1791,'http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/m_4a370660.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/x_e3e1362f.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/s_6273b611.jpg','В блендере смелите до мелкой крошки хлопья.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1791,'http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/m_316ddff9.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/x_9c14ab1b.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/s_e92768cd.jpg','Добавьте к хлопьям паприку, красный перец, соль, молотый перец. Перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1791,'http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/m_1fc593b3.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/x_6b5e40c0.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/s_412ef2f8.jpg','Курицу обваляйте в сухой смеси.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1791,'http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/m_831c9a1b.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/x_d1350822.jpg','http:\/\/cs5130.vkontakte.ru\/u4692577\/-14\/s_c3c47ac6.jpg','Нагрейте духовку до 220°C. Выложите на смазанный маслом противень курицу. Выпекайте 8-10 минут, затем переверните и подержите в духовке еще 5 минут.<br><br>Приятного аппетита!','');
