INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_66e32e09.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_3fdb27b2.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_65d5b88d.jpg','Заранее приготовить желе.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_9a765758.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_f2ef02cc.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_8f094444.jpg','Замочить 20 гр желатина в 10 ложках холодной воды на 10 минут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_2731a52e.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_67c755f4.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_75fd87d0.jpg','Затем распустить желатин на водяной бане до полного растворения крупинок, и пусть чуть приостынет.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_2d92338f.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_d43e9e8c.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_fa0baa48.jpg','Тем временем взбить 500 гр. сметаны с 1 ст. сахара, всыпать ванилин.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_72786ea2.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_4cc9f122.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_20e1c1c5.jpg','Затем немного сметанного крема, (буквально пару ложек) влить в желатин и не прекращая взбивать, тонкой струйкой влить эту смесь в крем.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_164fa82f.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_37a03237.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_9f3f8cd5.jpg','Далее: салатник выстелить пищевой пленкой','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_1465c922.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_10627a39.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_aa9bad29.jpg','И теперь в хаотичном порядке накидываем: кусочки наломанного бисквита и чайной ложечкой кусочки желе разного цвета.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_369b45e0.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_b26f4bc5.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_8a19cad0.jpg','Каждый слой поливаем сметанным кремом и так далее.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_46d7dbd1.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_c774b77e.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_f8b112f1.jpg','Затем последний слой — прикрыть целой бисквитной пышечкой и придавить слегка.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1850,'http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/m_d4f59af2.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/x_f69a4516.jpg','http:\/\/cs10027.vkontakte.ru\/u12766380\/-14\/s_4ee4d60f.jpg','Поставить в холодильник, чтобы застыл сметанный крем. Прижать салатник широким блюдом, перевернуть, снять салатник и аккуратно отлепить пленку.<br \/>\n<br \/>\nПриятного аппетита!','');
