INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1892,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_c2601450.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_dbb5c758.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_8656a84e.jpg','Смажьте соусом песто.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1892,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_6a43750e.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_37091adf.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_2dfaf035.jpg','Выложите сверху тонкие ломтики помидор.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1892,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_ea6cc25f.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_040cbb2c.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_cf72d53e.jpg','Поверх помидоров выложите тонкие кружочки сыра моцарелла.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1892,'http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/m_2ac24734.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/x_68f63379.jpg','http:\/\/cs305313.vkontakte.ru\/u6208554\/-14\/s_d4e2cc16.jpg','Отправьте пиццу, в разогретую до 210 градусов, духовку и выпекайте в течении 7-8 минут.<br \/>\n<br \/>\nПриятного аппетита!\n','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_0013f1ec.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_8716a2fd.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_ef89fd84.jpg','Очистите картофель и нарежьте. Отварите в подсоленной воде минут 20.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_13190a93.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_1ee548ed.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_c6997255.jpg','В это время растопите сливочное масло в сковороде. На среднем огне обжарьте нарезанный лук.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_7177b1dd.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_bdc2b380.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_5a8208b3.jpg','Когда лук начнет золотиться снимите его с огня, а на сковороду выложите фарш. Прожарьте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_7e088e7a.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_74961ce2.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_3dec0f7c.jpg','Верните лук на сковороду и добавьте горошек и кукурузу, Вустерширский соус, тимьян. Посолите и поперчите.Перемешайте и влейте говяжий бульон. Уменьшите огонь и готовьте без крышки около 10 минут. Духовку нагрейте до 200 градусов.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_c07ade81.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_5d40681d.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_eeef4802.jpg','Пока фарш тушиться, картофель слейте и толкушкой разомните картофель вместе со сливочным маслом, солью и перцем. Добавьте молоко и перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_209b5d40.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_28b83b4a.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_af672845.jpg','Фарш выложите на блюдо. Сверху закройте фарш картофельным пюре. Поставьте в духовку и готовьте 15-20 минут. Достаньте из духовки и посыпьте тертым сыром.Верните в духовку и готовьте еще 10 минут, чтобы сыр полностью расплавился.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1894,'http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/m_4e23920a.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/x_8f75a063.jpg','http:\/\/cs4351.vkontakte.ru\/u6624865\/-14\/s_7101d33e.jpg','Приятного аппетита!','');
