INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1897,'http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/m_43f736ab.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/x_a12a0178.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/s_ce93cea7.jpg','Перемешать в миске творог, натертый сыр, просеянную муку, сахар и соль.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1897,'http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/m_d9dc3032.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/x_1fdcb79a.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/s_5d7c8e7b.jpg','Месить тесто до тех пор пока не будет прилипает к пальцам.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1897,'http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/m_b7fdf31b.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/x_83e48823.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/s_94719f6f.jpg','Из готового теста скатать большую колбаску','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1897,'http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/m_69239875.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/x_1d560cc7.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/s_b1b9014d.jpg','Из колбаски нарезать будущие вареники так как показано на фото. Опустите вареники в кипящую подсоленную воду, аккуратно проведите ложкой по дну кастрюли, чтобы вареники к нему не приклеились, и варите их 1-2 минуты после всплывания.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1897,'http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/m_16201f7f.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/x_094a3d99.jpg','http:\/\/cs5808.vkontakte.ru\/u12766380\/-14\/s_b2604981.jpg','Готовые вареники выкладываем в тарелку и добавляем сливки и мед.<br \/>\n<br \/>\nПриятного аппетита!','');
