INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_05d8f471.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_d227d89f.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_b2711dd5.jpg','Добавьте немного воды, соль и протушить 5-7 минут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_080a3368.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_de3dd83d.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_0fe5041b.jpg','В миску добавьте яйца, сливки, соль, перец и немного цедры. Хорошенько все сбейте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_5ab5517c.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_6383bbde.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_f8e56116.jpg','Затем добавьте тертый сыр и снова хорошо перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_eddb885f.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_28faac91.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_952bc8e7.jpg','Процедите макароны и сразу добавьте во взбитые яйца.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_f341fee5.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_e2304796.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_bad1fb45.jpg','Выложить сверху горох, а поверх гороха - бекон.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_a8a2d818.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_3f7aa9b6.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_86d92cff.jpg','Затем выложите фрикадельки.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_acdfd03d.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_14bb5350.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_59852aac.jpg','И все хорошенько перемешиваем в последний раз.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1905,'http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/m_581f8f72.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/x_f10fdf3c.jpg','http:\/\/cs305312.vkontakte.ru\/u6208554\/-14\/s_a9c92687.jpg','Раскладываем по тарелкам, посыпаем тертым сыром и..блюдо готово!<br \/>\n<br \/>\nПриятного аппетита!','');
