INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_a839d2be.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_f71e459e.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_4148d0a7.jpg','Затем крупными кубиками нарежьте помидоры и добавьте в миску к салату.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_f1a729ae.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_0a3adc49.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_e3a2c2ca.jpg','Затем нарежьте лук полукольцами и добавьте в миску.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_159629e3.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_97a09676.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_93305809.jpg','Очищаем огурцы и режем мелкими кубиками.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_ad9ece80.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_721905cd.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_6f263812.jpg','Оливки разрезаем пополам. Часть оливок измельчите и отставьте пока в сторону. Мелко нарезаем зелень и вслед за огурцами добавляем все остальным ингредиентам.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_ebc0fa65.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_0148d102.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_c8d40ec0.jpg','Сыр фета разомните вилкой до мягкой консинстенции. Смешайте вместе с овощами и половинками оливок. Все хорошенько перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_6ced794a.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_6cc842d0.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_826627c8.jpg','Готовим заправку из оливкового масла, измельченных оливок, винного уксуса, размельченного зубчика чеснока и чайной ложки сахара. Все это хорошо перемешиваем.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_2f627ef3.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_9e0d8aae.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_089c4d21.jpg','Добавляем размельченные оливки и соус готов.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1925,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_b181282e.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_6680bd11.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_676e1137.jpg','Поливаем им овощи, перемешиваем и подаем. <br \/>\n<br \/>\nПриятного аппетита!\n','');
