INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(1975,'http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/m_f9b3c297.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/x_4a3518ce.jpg','http:\/\/cs4571.vkontakte.ru\/u6208554\/-14\/s_5115cd7e.jpg','Далее всыпьте коричневый сахар и горчицу. Влейте патоку и гранатовый сок и доведите до кипения. Варите до загустения','');
