INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_068865a2.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_93fa82f0.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_70e35030.jpg','Порежьте чеснок и добавьте его в блендер.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_6a84ee79.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_f6cf115b.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_2ed2f611.jpg','Добавьте в блендер соус, соль и перец.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_ac133b36.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_4218f305.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_ecbe71ae.jpg','Туда же добавьте кинзу, кайенский перец и красный перец.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_0dc9c0ea.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_62f6a984.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_9be0fbf1.jpg','Влейте оливковое масло, выжмите сок лимона и все хорошенько перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_b7e9084d.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_3242acbf.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_bfb3bf75.jpg','Залейте креветки подготовленным соусом и дайте им пропитаться в течении 20 минут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_be1776c5.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_e2b3da54.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_cc3af581.jpg','Нанизайте на шпажки и выложите на гриле (перед тем как нанизывать смочить шпажки в воде, чтобы в дальнейшем они не загорелись).','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_62d9467d.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_5a1002a0.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_e5d67bd8.jpg','Запекайте на гриле по 2-3 минуты с каждой стороны. В дополнение очень вкусно слегка поджарить ломтики ананасов.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2072,'http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/m_18fb88f7.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/x_1ec9f115.jpg','http:\/\/cs4574.vkontakte.ru\/u6208554\/-14\/s_f21a0176.jpg','Приятного аппетита!','');
