INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2255,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_406e356d.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_d8244d6e.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_71068b98.jpg','Высыпьте сухарики в отдельную миску и отставьте в сторонку. Подготовьте противень и накройте его пергаментной бумагой.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2255,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_5e527a49.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_d27cfe3c.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_7e11b77c.jpg','Возьмите треску и выжмите на нее сок лимона','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2255,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_31b81f4c.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_445fc343.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_f5632ec1.jpg','Посолите и поперчите. Нарежьте вдоль длинными полосками','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2255,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_7c2438a8.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_62f65520.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_0665827d.jpg','Нарежьте каждую полоску на кусочки, старайтесь делать их равномерными','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2255,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_0f80ce91.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_64f3b983.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_33b56042.jpg','Обмакните кусочки в яичную смесь. Затем обваляйся в панировочных сухарях со всех сторон','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2255,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_345fc5db.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_f52274ff.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_580e82b1.jpg','Выложите рыбные кусочки на противень и разогрейте духовку до 200 градусов. Выпекайте в течении 10 минут до золотистого цвета','');
