INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2323,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_2854e361.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_7e7ddb7e.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_a19463bc.jpg','Мочим руки, берем рис и аккуратно уминаем его, формируя \"колбаску\"','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2323,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_2396cd18.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_4b9304aa.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_2fdd34ea.jpg','Выкладываем суши-меши на лист водоросли нори и разглаживаем формируя ровный слой риса на листе нори.  Аккуратно переворачиваем лист нори с рисом пополам, подготавливая площадку для укладывания ингредиентов.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2323,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_b24f2230.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_e14bb621.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_188565bf.jpg','Из майонеза выкладываем «дорожку», поверх которой кладем крабовое мясо','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2323,'http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/m_bde9655d.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/x_c845d6c5.jpg','http:\/\/cs11439.vkontakte.ru\/u6208554\/-14\/s_7dacd3fd.jpg','Очищаем авокадо. Режем тоненькими дольками и укладываем поверх крабового мяса. ','');
