INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2454,'http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/m_4823c808.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/x_d598afe4.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/s_c8cbeaa7.jpg','Сыр и сливочное масло натереть на мелкой терке и тщательно перемешать. (можете положить тмин или кунжут по желанию)','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2454,'http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/m_85508275.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/x_8e78cf0f.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/s_ab3f4ed8.jpg','Добавить соль, молоко и цедру и ещё раз тщательно перемешать.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2454,'http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/m_f6a3f774.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/x_8515cf3c.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/s_edeaa758.jpg','Затем добавьте всю муку и замесите мягкое и эластичное тесто.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2454,'http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/m_7c3e1884.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/x_e6ff30c8.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/s_6bc4c844.jpg','Раскатайте тесто в пласт, толщиной 0,5 см, нарежьте его на полосочки и каждую полосочку скатайте пальчиками в трубочку.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2454,'http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/m_9b836e40.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/x_9fc3cfcf.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/s_e3648600.jpg','Уложите их на пергаментную бумагу и выпекайте 15 минут при 180 градусах, до золотистого цвета.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2454,'http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/m_af2c8e33.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/x_0102e11a.jpg','http:\/\/cs303608.vkontakte.ru\/u14449697\/-14\/s_fd9e1bfa.jpg','Остудите и кушайте! Сырный вкус проявится, когда они остынут!','');
