INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2560,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_7c77dcb6.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_373ddbf5.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_e2523e17.jpg','Моем гранат. На этом этапе желательно повязать фартук, чтобы уберечь одежду от случайных брызг сока.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2560,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_5a99fca1.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_041e16b1.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_aa576bde.jpg','Срезаем ножом верхушку с хвостиком так, чтобы в срезе появились белые прожилки.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2560,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_02148504.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_9f734349.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_32efa5bb.jpg','Вдоль этих светлых прожилок делаем неглубокие надрезы.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2560,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_8cf2406e.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_10979830.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_4fa863b3.jpg','Переворачиваем гранат – срезом вниз. Берем столовую ложку и начинаем постукивать по кожуре. Постукивания уверенные и равномерные – и зерна гранатовые начнут прыгать в посудину. Не бойтесь повредить зерна.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2560,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_3d466eb3.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_d3589423.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_a4952da0.jpg','В итоге – тарелка с готовым к употреблению гранатом и цветок гранатовый!','');
