INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_19efcaf7.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_fb23a3e3.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_e81486b4.jpg','На оливковом масле потушите овощи в течение 10-15 минут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_3e178676.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_53af4cf6.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_39896572.jpg','Остудите, охладите  в холодильнике около часа.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_66999bab.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_3ff3491a.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_67db5943.jpg','Пока овощи охлаждаются, замочите сухари в молоке или бульоне, пока они не размягчатся.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_942e6bc2.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_b303acf6.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_8292069f.jpg','Все остальные ингредиенты смешайте с размягченным хлебом.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_87b9c090.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_acbdff42.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_05cbf42a.jpg','Добавьте тушеные овощи и перемешайте все до однородной массы. <br>Разделите массу на 2 части и слепите 2 рулета. Выложите на блюдо для выпекания, залейте соусом барбекю и распределите его по всей поверхности.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_db213789.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_52ee92c9.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_104429ec.jpg','Выпекайте около часа при 175ºC.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2568,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_0c24905b.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_76b920e0.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_795eb250.jpg','Перед тем как нарезать и подавать, оставьте в духовке на 20 минут.','');
