INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2589,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_21c30a05.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_0de3f099.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_a7a15c38.jpg','Сахар смешайте с имбирем, и эту массу выложите на свежий грейпфрут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2589,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_2453a4e2.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_f8641c17.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_4dba0332.jpg','Поставьте в духовку и выпекайте под включенным в духовке бройлером 5-7 минут до состояния, когда сахар начинает карамелизоваться. Немедля подавайте.','');
