INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_fcc84b12.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_af8a5910.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_5f2a4dfc.jpg','','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_360bac13.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_975dfecd.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_edd81e44.jpg','Сразу поставьте на огонь воду для спагетти, а затем приступайте к приготовлению соуса.<br>Измельчите чеснок и перчики чили. Если хотите меньше остроты — не забудьте удалить семена. Обжарьте все пару минут на оливковом масле.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_fac117fe.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_4ec6c390.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_7f818bc8.jpg','Спелые помидоры очистите от кожицы, предварительно сделав на ней крестообразный надрез и опустив в кипяток на минуту.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_4229a86d.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_ddd3fa6c.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_0696082b.jpg','Хорошо измельчите помидоры и добавьте в сковороду к чесноку и перцу, доведите до кипения и дайте покипеть минут 5 на небольшом огне.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_617f7389.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_991cbe75.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_ee10061f.jpg','Добавьте каперсы и нарезанные маслины, посолите и готовьте еще 5 минут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_bc0b467f.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_6d6c9b42.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_fbede28e.jpg','За минуту до конца приготовления соуса положите в него нарезанный базилик.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2615,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_24acdb73.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_21978fdc.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_33374fba.jpg','Спагетти сварите до состояния аль денте, слейте воду и сбрызните оливковым маслом.<br>Горячие спагетти положите на тарелку, а сверху выложите соус.<br><br>Приятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2616,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_f8c74a75.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_1b486bbf.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_1effa728.jpg','','');
