INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_fa4853f0.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_300e894d.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_0acea67f.jpg','Яйца, сливки, молоко, корицу, ванилин,белый сахар взбейте до однородной массы. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_404b42d2.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_4dd0747c.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_f648b9ef.jpg','Подготовьте 5-6 небольших керамических формочек (в зависимости от величины буханки хлеба) и смажьте изнутри сливочным маслом. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_845455e8.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_8e780fd0.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_ebe5b435.jpg','Обмокните каждый кусок хлеба в яично-молочной смеси, затем слегка обваляйте в коричневом сахаре (2 столовых ложки).','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_6a026468.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_81cbecc6.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_f36a1763.jpg','Выложите в формочку по 3 куска подготовленного хлеба, между которыми положите по кусочку нарезанного банана.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_4037fce2.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_6a8bf8fa.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_6e094387.jpg','Поставьте на блюдо, затем в холодильник на час минимум (на ночь). Перед тем как выпекать смешайте оставшиеся 4 столовых ложки коричневого сахара со столовой ложкой сливочного масла и смажьте хлеб в каждой формочке. Выпекайте при 175°C 30-35 минут.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_5eb3ff3d.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_1a5202d4.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_01643598.jpg','Присыпьте сахарной пудрой и подавайте с кленовым сиропом или медом.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2652,'http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/m_6603a5bc.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/x_64f92b95.jpg','http:\/\/cs11515.vkontakte.ru\/u6624865\/-14\/s_d4d905d2.jpg','Приятного аппетита!','');
