INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_af843cc9.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_1547da39.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_fa283b8f.jpg','Очистите опята, положите их в подсоленную воду и доведите до кипения. Варите 10-12 минут с момента закипания, затем слейте воду. Опята нарежьте на 2-3 части.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_3dcec8a0.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_eb910a28.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_143cbd14.jpg','Нарезанный короткой соломкой лук обжарьте в масле 1-2 минуты на среднем огне до прозрачности.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_28c90e9e.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_fa8e96db.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_7a24aec1.jpg','Добавьте очищенную и нарезанную кубиками картошку, перемешайте и готовьте 15 минут, периодически помешивая. Посолите и поперчите.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_fbf62c09.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_3cd3dc42.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_03876ea6.jpg','Картошка должна стать мягкой, а лук золотистым.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_2abed76a.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_384a742b.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_3020f5fe.jpg','Отваренные грибы положите в сковороду к картошке.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_3f991828.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_1626adf5.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_97d85735.jpg','Добавьте сметану и перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2662,'http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/m_545b8a60.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/x_28bc2449.jpg','http:\/\/cs11190.vkontakte.ru\/u6208554\/-14\/s_548d1448.jpg','Накройте крышкой и готовьте на маленьком огне 5-7 минут. <br \/>\n<br \/>\nПриятного аппетита!','');
