INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2741,'http:\/\/cs11498.vkontakte.ru\/u6624865\/-14\/m_80f31d58.jpg','http:\/\/cs11498.vkontakte.ru\/u6624865\/-14\/x_f98d9f89.jpg','http:\/\/cs11498.vkontakte.ru\/u6624865\/-14\/s_980aea82.jpg','Совместите 2 куска хлеба, вырежьте формой.','');
