INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(2904,'http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/m_5fdb775f.jpg','http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/x_5029d866.jpg','http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/s_49b00ae2.jpg','Снимите с огня и быстро вмешайте мелко натертый шоколад.','');
