INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3056,'http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/m_6c5a0301.jpg','http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/x_283514ef.jpg','http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/s_0f382702.jpg','Очистите картофель и сельдерей, нарежьте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3056,'http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/m_b4335632.jpg','http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/x_a66465f3.jpg','http:\/\/cs11476.vkontakte.ru\/u6624865\/-14\/s_d2c3f2dc.jpg','Отварите картофель и сельдерей.<br>Разомните со сливочным маслом. Добавляйте одновременно молоко и йогурт по столовой ложке, достигая желаемой консистенции. Посолите и поперчите по вкусу.','');
