INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3210,'http:\/\/cs5140.userapi.com\/u46279314\/-14\/m_e3b21bdf.jpg','http:\/\/cs5140.userapi.com\/u46279314\/-14\/x_d8932591.jpg','http:\/\/cs5140.userapi.com\/u46279314\/-14\/s_a6908103.jpg','-Добавляем нарезанную брусочками тыкву и готовим на небольшом огне минут 5.','');
