INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3404,'http:\/\/cs5322.userapi.com\/u46279314\/-14\/m_911cadc2.jpg','http:\/\/cs5322.userapi.com\/u46279314\/-14\/x_e1f5c943.jpg','http:\/\/cs5322.userapi.com\/u46279314\/-14\/s_cc6bca01.jpg','Добавьте промытые, очищенные и нарезанные белые грибы.','');
