INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3459,'http:\/\/cs5984.userapi.com\/u6624865\/-14\/m_b8c69cf6.jpg','http:\/\/cs5984.userapi.com\/u6624865\/-14\/x_fb840d09.jpg','http:\/\/cs5984.userapi.com\/u6624865\/-14\/s_8d21297f.jpg','Посолите по вкусу.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3459,'http:\/\/cs5984.userapi.com\/u6624865\/-14\/m_50682738.jpg','http:\/\/cs5984.userapi.com\/u6624865\/-14\/x_bacc82d7.jpg','http:\/\/cs5984.userapi.com\/u6624865\/-14\/s_a631b807.jpg','Перемешайте отваренные спагетти с приготовленным соусом. Выложите на тарелку и сверху присыпьте пармезаном и парой орешков.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3459,'http:\/\/cs5984.userapi.com\/u6624865\/-14\/m_edcdd1d4.jpg','http:\/\/cs5984.userapi.com\/u6624865\/-14\/x_99f6188e.jpg','http:\/\/cs5984.userapi.com\/u6624865\/-14\/s_cb57ac1d.jpg','','');
