INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3516,'http:\/\/cs6000.userapi.com\/u6208554\/-14\/m_81316f00.jpg','http:\/\/cs6000.userapi.com\/u6208554\/-14\/x_71304305.jpg','http:\/\/cs6000.userapi.com\/u6208554\/-14\/s_760ed140.jpg','Каждый кусок хлеба намажьте маслом с обеих сторон.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3516,'http:\/\/cs6000.userapi.com\/u6208554\/-14\/m_a97e1250.jpg','http:\/\/cs6000.userapi.com\/u6208554\/-14\/x_f116abd7.jpg','http:\/\/cs6000.userapi.com\/u6208554\/-14\/s_5af90774.jpg','Подогрейте сковороду на среднем огне. Затем обжарьте каждый кусок хлеба с обеих сторон. Секрет в том, что тонкий слой масла сделает корочку хрустящей.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(3516,'http:\/\/cs6000.userapi.com\/u6208554\/-14\/m_7e7399ed.jpg','http:\/\/cs6000.userapi.com\/u6208554\/-14\/x_960da409.jpg','http:\/\/cs6000.userapi.com\/u6208554\/-14\/s_dbd16b4a.jpg','Затем положите сыр.','');
