INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4299,'http:\/\/cs11403.userapi.com\/u46279314\/-14\/m_39ced3bb.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/x_2ad71648.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/s_a40fb110.jpg','Помещаем тесто в подготовленные формы.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4299,'http:\/\/cs11403.userapi.com\/u46279314\/-14\/m_b01271b2.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/x_6c5281c2.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/s_a9beaf2f.jpg','Затем смешиваем томатную пасту с любимыми специями и помещаем равномерным слоем на тесто.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4299,'http:\/\/cs11403.userapi.com\/u46279314\/-14\/m_5904720f.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/x_4e1d185d.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/s_f38e5f4e.jpg','Затем выкладываем мясо или колбасу (также можно добавить кукурузу или ананасы).','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4299,'http:\/\/cs11403.userapi.com\/u46279314\/-14\/m_b40d1ac8.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/x_a5324da1.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/s_b4acf73c.jpg','Затем покрываем слоем тертого сыра.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4299,'http:\/\/cs11403.userapi.com\/u46279314\/-14\/m_bdabc718.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/x_548a6072.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/s_a3874eea.jpg','После чего на самый верх выкладываем пеперонни или ананасы. (если ананасы, то стоит их покрыть еще тонким слоем сыра)','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4299,'http:\/\/cs11403.userapi.com\/u46279314\/-14\/m_a5c5ac03.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/x_325a0850.jpg','http:\/\/cs11403.userapi.com\/u46279314\/-14\/s_fcc02470.jpg','Помещаем все в разогретую до 200С духовку на 15-18 минут.<br \/>\n<br \/>\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4302,'http:\/\/cs5976.userapi.com\/u4692577\/-14\/m_ccf8feae.jpg','http:\/\/cs5976.userapi.com\/u4692577\/-14\/x_37c61376.jpg','http:\/\/cs5976.userapi.com\/u4692577\/-14\/s_f79ca6d9.jpg','','');
