INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4375,'http:\/\/cs10077.userapi.com\/u6624865\/-14\/m_402020ea.jpg','http:\/\/cs10077.userapi.com\/u6624865\/-14\/x_d89adec3.jpg','http:\/\/cs10077.userapi.com\/u6624865\/-14\/s_5f08cd5b.jpg','','');
