INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4563,'http:\/\/cs5965.userapi.com\/u33085317\/-14\/m_0028f011.jpg','http:\/\/cs5965.userapi.com\/u33085317\/-14\/x_902eb732.jpg','http:\/\/cs5965.userapi.com\/u33085317\/-14\/s_4da617d6.jpg','Добавить специи по вкусу.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4563,'http:\/\/cs5965.userapi.com\/u33085317\/-14\/m_fd4745ed.jpg','http:\/\/cs5965.userapi.com\/u33085317\/-14\/x_05300798.jpg','http:\/\/cs5965.userapi.com\/u33085317\/-14\/s_afe0dcb0.jpg','Уложить в форму для запекания и отправить в духовку на 25 минут при 180 градусах.<br \/>\n<br \/>\nПриятного аппетита!','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4565,'http:\/\/cs302403.userapi.com\/u46279314\/-14\/m_dfe4e29a.jpg','http:\/\/cs302403.userapi.com\/u46279314\/-14\/x_feb7bd9f.jpg','http:\/\/cs302403.userapi.com\/u46279314\/-14\/s_cabc8d9e.jpg','','');
