INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_47890c22.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_5dc38841.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_5bbda612.jpg','','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_17302404.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_c632da85.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_ac12180a.jpg','Разогрейте духовку до 220 градусов. В миске смешайте сметану, сахар, ванилин и 2 яйца.\n','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_80cd1d91.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_6ca61b54.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_19346db9.jpg','Добавьте соду, гашеную уксусом.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_d5d6534e.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_cf522590.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_5713b801.jpg','Добавьте просеянную муку. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_aa0f47f8.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_08d43cbd.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_63bd9c22.jpg','Перемешайте.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_fe2ff2c1.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_47f36969.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_97d0dbc2.jpg','Поломайте плитку шоколада и разморозьте ягоды.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(4845,'http:\/\/cs10409.userapi.com\/u33085317\/-14\/m_71dc097f.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/x_4608fd0b.jpg','http:\/\/cs10409.userapi.com\/u33085317\/-14\/s_38dd3fa0.jpg','Шоколад смешайте с тестом. Выложите в форму половину теста, посыпьте ягодами. Влейте оставшееся тесто и добавьте чернику.','');
