INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(5512,'http:\/\/cs302111.userapi.com\/u33085317\/-14\/m_a8cf9493.jpg','http:\/\/cs302111.userapi.com\/u33085317\/-14\/x_da6bb08b.jpg','http:\/\/cs302111.userapi.com\/u33085317\/-14\/s_a453d9ae.jpg','Оберните бутерброды в фольгу и выпекайте около 15 минут при 180 градусах.','');
