INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6355,'http:\/\/cs5682.userapi.com\/u1919081\/-14\/m_612e397b.jpg','http:\/\/cs5682.userapi.com\/u1919081\/-14\/x_f5db6d28.jpg','http:\/\/cs5682.userapi.com\/u1919081\/-14\/s_525c2d75.jpg','Посыпаем тесто оставшимся сахаром.','');
