INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/570\/woZvYv78B58.jpg','http:\/\/cs11016.userapi.com\/v11016081\/571\/ZZmR8aumuxg.jpg','http:\/\/cs11016.userapi.com\/v11016081\/56f\/xr6Am08F2WU.jpg','Удаляем сердцевинки из перцев.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/577\/WYdz57tlTLg.jpg','http:\/\/cs11016.userapi.com\/v11016081\/578\/GiRZ6D6n_0U.jpg','http:\/\/cs11016.userapi.com\/v11016081\/576\/uWWccs-twRM.jpg','Лук режем небольшими кубиками и обжариваем в небольшом количестве растительного масла до золотистого цвета (~10 минут)','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/57e\/lR0jKD5dRLs.jpg','http:\/\/cs11016.userapi.com\/v11016081\/57f\/fnHQHKh5dzA.jpg','http:\/\/cs11016.userapi.com\/v11016081\/57d\/bo3egFkVnJM.jpg','За 2-3 минуты до готовности добавляем рубленный зеленый лук.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/585\/oJ5Zv3zMvu4.jpg','http:\/\/cs11016.userapi.com\/v11016081\/586\/KVHAI-zwwv8.jpg','http:\/\/cs11016.userapi.com\/v11016081\/584\/I_nlhJS_S-4.jpg','Булгур, фарш и лук помещаем в миску и смешиваем все вместе.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/58c\/LLugA45qpCU.jpg','http:\/\/cs11016.userapi.com\/v11016081\/58d\/NF_VomIcwD4.jpg','http:\/\/cs11016.userapi.com\/v11016081\/58b\/9ILW3qH3jJ4.jpg','Добавляем соль и молотый черный перц, перемешиваем.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/593\/ZGWgXCSckEQ.jpg','http:\/\/cs11016.userapi.com\/v11016081\/594\/_rEeJeCgdks.jpg','http:\/\/cs11016.userapi.com\/v11016081\/592\/uIhuh-h_bq0.jpg','Начиняем перцы получившейся массой, закрываем \"крышечками\" и помещаем их в кастрюлю.\n\n','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(6743,'http:\/\/cs11016.userapi.com\/v11016081\/59a\/ADZJtuCvhfM.jpg','http:\/\/cs11016.userapi.com\/v11016081\/59b\/56un1gnqLvU.jpg','http:\/\/cs11016.userapi.com\/v11016081\/599\/prUfthgR7cs.jpg','Заливаем бульоном и готовим под крышкой 30 -35 минут, на среднем огне.','');
