INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(7020,'http:\/\/cs305504.userapi.com\/v305504865\/2e3\/_yTXl-5HJys.jpg','http:\/\/cs305504.userapi.com\/v305504865\/2e4\/Qb8NFHzul_I.jpg','http:\/\/cs305504.userapi.com\/v305504865\/2e2\/92SQt1txA0M.jpg','Выложите основу для пиццы на противень и выпекайте 6-10 минут до бледно-золотистой корочки.','');
