INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(7157,'http:\/\/cs305504.userapi.com\/v305504865\/871\/Kc0HwxVVydE.jpg','http:\/\/cs305504.userapi.com\/v305504865\/872\/mmnU4dMiLyE.jpg','http:\/\/cs305504.userapi.com\/v305504865\/870\/Lh-VMZ7GcDw.jpg','Духовку нагрейте до 175 градусов. Картофель вымойте и очистите. Тонко нарежьте, выложите в сковороду и присыпьте солью, перцем, сбрызните оливковым маслом.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(7157,'http:\/\/cs305504.userapi.com\/v305504865\/879\/7a59AvVnCas.jpg','http:\/\/cs305504.userapi.com\/v305504865\/87a\/EvDD_ihTd3E.jpg','http:\/\/cs305504.userapi.com\/v305504865\/878\/hgn1bIDOX5o.jpg','Выдавите чеснок и забросьте его в сковороду.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(7157,'http:\/\/cs305504.userapi.com\/v305504865\/888\/2RtQD7KAeSo.jpg','http:\/\/cs305504.userapi.com\/v305504865\/889\/YAqmFRLEML4.jpg','http:\/\/cs305504.userapi.com\/v305504865\/887\/SAIJADmpRv4.jpg','Обжарьте картофель около 5 минут, затем переложите в блюдо для запекания, перемешайте с соусом песто и поставьте в духовку на 5-10 минут. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(7157,'http:\/\/cs305504.userapi.com\/v305504865\/890\/nsvw1acevCg.jpg','http:\/\/cs305504.userapi.com\/v305504865\/891\/IBIO0of6sso.jpg','http:\/\/cs305504.userapi.com\/v305504865\/88f\/fwqP4MX5_Ck.jpg','Готовьте до хрустящей корочки. Разбейте поверх яйца и держите в духовке до готовности яиц. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(7157,'http:\/\/cs305504.userapi.com\/v305504865\/898\/yh-v7UM1CZQ.jpg','http:\/\/cs305504.userapi.com\/v305504865\/899\/bbDNSgX75Ro.jpg','http:\/\/cs305504.userapi.com\/v305504865\/897\/QxwhM0PaVXI.jpg','Украсьте базиликом.','');
