INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(8971,'http:\/\/cs10711.userapi.com\/v10711577\/25c7\/B6l49m-w6xM.jpg','http:\/\/cs10711.userapi.com\/v10711577\/25c8\/Ks4hk6aqoTo.jpg','http:\/\/cs10711.userapi.com\/v10711577\/25c6\/NFaL6G8xnDI.jpg','Поджариваем колбасу, избавляем ее от излишков масла пергаментной бумагой, выкладываем на тесто.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(8971,'http:\/\/cs10711.userapi.com\/v10711577\/25ce\/hTo6AN98gyU.jpg','http:\/\/cs10711.userapi.com\/v10711577\/25cf\/FhdNuXP6GBY.jpg','http:\/\/cs10711.userapi.com\/v10711577\/25cd\/Rcx7njuSGOE.jpg','Слегка взбиваем яйца и заливаем ими слой колбасы.','');
