INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(9067,'http:\/\/cs301512.userapi.com\/v301512865\/1057\/vLF_04GojC0.jpg','http:\/\/cs301512.userapi.com\/v301512865\/1058\/rV7STuQuT9g.jpg','http:\/\/cs301512.userapi.com\/v301512865\/1056\/eOLTV1m2l64.jpg','Выдавите сок из лимона, на 1 чашку сока уходит примерно 4 крупных сочных лимона.','');
