INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(9473,'http:\/\/cs303301.userapi.com\/v303301554\/18b7\/UPRMv6dRPHI.jpg','http:\/\/cs303301.userapi.com\/v303301554\/18b8\/RZTkZC_Rjh4.jpg','http:\/\/cs303301.userapi.com\/v303301554\/18b6\/2VfAWLUkYQY.jpg','','');
