INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(9569,'http:\/\/cs410927.userapi.com\/v410927865\/8df\/M63cEcxzL5E.jpg','http:\/\/cs410927.userapi.com\/v410927865\/8e0\/9FdSTSu88Ac.jpg','http:\/\/cs410927.userapi.com\/v410927865\/8de\/6K_NJnz7KG4.jpg','Всыпьте кускус и снимите кастрюлю с огня. Закройте крышкой и оставьте пока крупа не впитает всю жидкость 5-7 минут. Вилкой перемешайте кускус, чтобы разбить комки. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(9569,'http:\/\/cs410927.userapi.com\/v410927865\/8e7\/JZ6JL8oq9xM.jpg','http:\/\/cs410927.userapi.com\/v410927865\/8e8\/uxHKbIOJTEA.jpg','http:\/\/cs410927.userapi.com\/v410927865\/8e6\/tJek_8i4Bic.jpg','Выложите кускус на блюдо и сбрызните оливковым маслом. ','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(9569,'http:\/\/cs410927.userapi.com\/v410927865\/8ef\/cPi_e4hkrEA.jpg','http:\/\/cs410927.userapi.com\/v410927865\/8f0\/LUTfuZlbcWY.jpg','http:\/\/cs410927.userapi.com\/v410927865\/8ee\/gXc2GNcxNII.jpg','Добавьте орехи и тщательно перемешайте.','');
