INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(11869,'http:\/\/cs9936.userapi.com\/u1764781\/-14\/m_974be7f8.jpg','http:\/\/cs9936.userapi.com\/u1764781\/-14\/x_6f53afce.jpg','http:\/\/cs9936.userapi.com\/u1764781\/-14\/s_148de9a6.jpg','На деревянную шпажку одеть яичко, затем помидор. Повторить процедуру.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(11869,'http:\/\/cs9936.userapi.com\/u1764781\/-14\/m_da5a372d.jpg','http:\/\/cs9936.userapi.com\/u1764781\/-14\/x_da8a6751.jpg','http:\/\/cs9936.userapi.com\/u1764781\/-14\/s_05754231.jpg','Украсить яички листьями петрушки. При помощи майонеза сделать на «шляпках» пятнышки. <br\/>\n<br\/>\nПриятного аппетита!','');
