INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12349,'http:\/\/cs6104.userapi.com\/v6104380\/227c\/TQ4UTBQoc7I.jpg','http:\/\/cs6104.userapi.com\/v6104380\/227d\/FtlkO7KouxM.jpg','http:\/\/cs6104.userapi.com\/v6104380\/227b\/d5gxXcT9uJ0.jpg','Когда фарш с колбасой хорошо поджарятся влейте соус и перемешайте.','');
