INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12349,'http:\/\/cs6104.userapi.com\/v6104380\/228a\/TmrxlCw-8Js.jpg','http:\/\/cs6104.userapi.com\/v6104380\/228b\/NZ-FPNJpdJk.jpg','http:\/\/cs6104.userapi.com\/v6104380\/2289\/6fVPrHtyfHM.jpg','Выложите тесто для лазаньи в формочки и разложите творог со специями.','');
