INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12452,'http:\/\/cs410719.userapi.com\/v410719380\/30cd\/Jh_tZ7Nhuzs.jpg','http:\/\/cs410719.userapi.com\/v410719380\/30ce\/BbLQ9TssRDM.jpg','http:\/\/cs410719.userapi.com\/v410719380\/30cc\/3viBpm5qCd0.jpg','Отделяем белки от желтков.','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12452,'http:\/\/cs410719.userapi.com\/v410719380\/30d4\/vP70E_JeCGY.jpg','http:\/\/cs410719.userapi.com\/v410719380\/30d5\/9gj1c7OinVY.jpg','http:\/\/cs410719.userapi.com\/v410719380\/30d3\/VqCEngELXwA.jpg','Начинаем взбивать белки на самой маленькой скорости миксера. Медленно, по одной ложке добавляем сахар и всыпаем ванилин (на кончике ножа).','');
INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12452,'http:\/\/cs410719.userapi.com\/v410719380\/30db\/h9hqvQrPTaA.jpg','http:\/\/cs410719.userapi.com\/v410719380\/30dc\/n4T3l2xhPMc.jpg','http:\/\/cs410719.userapi.com\/v410719380\/30da\/7N2gcUc2cBI.jpg','Добавляем желтки.','');
